SQL_INJECTION:
SELECT c.customer_code, SUM(oi.quantity) AS total_quantity, AVG(oi.unit_price) AS avg_price 
FROM sales_db.customers c 
JOIN sales_db.orders o ON o.customer_id = c.customer_id 
JOIN sales_db.order_items oi ON oi.order_id = o.order_id 
JOIN sales_db.products p ON p.product_id = oi.product_id 
WHERE o.status = '{status}' AND p.category_id = {category_id} 
GROUP BY c.customer_code 
HAVING total_quantity > {min_quantity} 
LIMIT {limit};

UNION_ATTACK:
SELECT o.order_id, p.product_name, oi.quantity 
FROM orders o
JOIN order_items oi ON oi.order_id = o.order_id
JOIN products p ON p.product_id = oi.product_id
WHERE o.status = '{status}' AND oi.quantity > {threshold}
LIMIT {limit};

SELECT o.customer_id, p.product_name, SUM(oi.quantity * oi.unit_price) AS total_revenue
FROM orders o
JOIN order_items oi ON oi.order_id = o.order_id
JOIN products p ON p.product_id = oi.product_id
WHERE o.status = '{status}' AND oi.quantity > {min_qty}
GROUP BY o.customer_id, p.product_name
HAVING COUNT(*) >= {min_orders}
LIMIT {limit};

