CHECK_STOCK:
SELECT available_stock
FROM inventory_levels
WHERE product_id = {product_id}
  AND (current_stock >= min_stock_level AND current_stock <= max_stock_level)
ORDER BY available_stock DESC
LIMIT {limit};

SELECT available_stock
FROM inventory_levels
WHERE product_id = '{product_id}';

SELECT il.product_id, il.location_id, il.current_stock, il.available_stock, il.min_stock_level, il.max_stock_level,
       CASE WHEN il.available_stock <= il.min_stock_level THEN 'low' ELSE 'normal' END AS stock_status,
       il.last_updated
FROM inventory_db.inventory_levels il
WHERE il.product_id = {product_id}
  AND il.location_id = {location_id}
  AND il.available_stock <= il.min_stock_level
ORDER BY il.location_id, il.product_id
LIMIT {limit};

SELECT il.location_id, il.product_id, il.available_stock, il.current_stock, il.min_stock_level, il.max_stock_level, il.last_updated
FROM inventory_levels il
WHERE il.product_id = {product_id}
  AND il.location_id IN (SELECT location_id FROM warehouse_locations WHERE status = 'active')
  AND (il.available_stock < il.min_stock_level OR il.available_stock > il.max_stock_level)
LIMIT {limit};

SELECT i.location_id, i.product_id, i.available_stock, i.current_stock, i.min_stock_level, i.max_stock_level, 
       CASE WHEN i.current_stock <= i.min_stock_level THEN 'Reorder Needed' ELSE 'In Stock' END AS stock_status
FROM inventory_levels i
WHERE i.product_id = {product_id}
  AND i.location_id = {location_id}
  AND i.current_stock <= i.max_stock_level
LIMIT {limit};

SELECT available_stock
FROM inventory_levels
WHERE product_id = {product_id}
  AND location_id = {location_id}
ORDER BY available_stock DESC
LIMIT {limit};

SELECT il.product_id, il.location_id, il.current_stock, il.available_stock, 
       CASE WHEN il.current_stock < il.min_stock_level THEN 'LOW' ELSE 'OK' END AS reorder_status
FROM inventory_db.inventory_levels il
WHERE il.product_id = {product_id}
  AND il.location_id = {location_id}
  AND il.current_stock < il.min_stock_level
ORDER BY il.location_id, il.product_id;

SELECT il.product_id, il.location_id, il.current_stock, il.available_stock, il.min_stock_level, il.max_stock_level, (il.current_stock - il.min_stock_level) AS reorder_indicator
FROM inventory_levels il
WHERE il.product_id = {product_id} AND il.location_id = {location_id}
ORDER BY il.location_id, il.product_id, il.current_stock DESC;

SELECT available_stock
FROM inventory_levels
WHERE product_id = {product_id}
  AND location_id = {location_id}
ORDER BY available_stock DESC;

SELECT il.location_id, il.product_id, il.available_stock, il.current_stock, il.min_stock_level, il.max_stock_level 
FROM inventory_levels il 
WHERE il.product_id = {product_id} 
  AND il.location_id IN (SELECT location_id FROM inventory_levels WHERE product_id = {product_id} AND available_stock < min_stock_level) 
ORDER BY il.location_id, il.product_id 
LIMIT {limit};

