CHECK_INVENTORY:
SELECT c.contact_name, p.product_name, i.quantity 
FROM inventory i JOIN product p ON i.product_id = p.product_id JOIN sales_db.check_inventory ci ON i.inventory_id = ci.inventory_id 
JOIN customers c ON ci.customer_id = c.customer_id WHERE c.company_name = 'Johns Limited';

SELECT c.customer_name, p.product_name, i.quantity 
FROM products p LEFT JOIN inventory i ON p.product_id = i.product_id 
LEFT JOIN sales_db.check_inventory ci ON i.product_id = ci.product_id ORDER BY p.product_name;

SELECT c.contact_name, p.product_name, i.quantity 
FROM products p INNER JOIN inventory i ON p.product_id = i.product_id 
INNER JOIN sales_sales sale ON i.product_id = sale.product_id GROUP BY c.contact_name;

