CHECK_PAYMENT:
SELECT 
    IF(
        ( 
            SELECT COUNT(*) 
            FROM invoices i 
            JOIN payments p ON i.invoice_id = p.invoice_id 
            WHERE i.customer_id = 1 AND i.total_amount - SUM(p.amount) < 0
        ) IS TRUE, 
        'CHECK_PAYMENT', 'NO_CHECK' , 'CHECK_PAYMENT'
    ) AS CHECK_PAYMENT;

